import axios from 'axios';

const url = 'http://localhost:5000'



// AUTH
export const createUser = (newUser) => axios.post(`${url}/signup`, newUser);
export const authUser = (userInfo) => axios.post(`${url}/login`, userInfo);
export const isUserLogged = () => axios.get(`http://localhost:5000/login`)


//PRODUCT 
export const createProduct =  async(newProduct)=> axios.post(`${url}/product/new`, newProduct);
export const fetchProducts = () => axios.get(`${url}/product`)
export const fetchSingleProduct = (id) => axios.get(`${url}/product/${id}`)
