import React,{useState, useEffect} from 'react'
import {Card,Button,Collapse} from 'react-bootstrap'
import ReadMoreReact from 'read-more-react';
import {NavLink } from 'react-router-dom'

import ProductScreen from '../screens/ProductScreen'


const moreTxt = () =>{
    return(<div style={{fontSize:'1rem',color:'#0069d9'}}>Read more...</div>)
}



const Product = (product) =>{
    const [readMore,setReadMore]=useState(false);
    const [disabledButton,setDisabledButton]=useState(false)
    const productPath = `/product/${product.id}`
    const clickHandler = () =>{
        let products = [];
        if(localStorage.getItem('products')){
            products = JSON.parse(localStorage.getItem('products'));
        }
        products.push({productId : product.id, image : product.img, name:product.name,price:product.price});
        localStorage.setItem('products', JSON.stringify(products));

    }


    useEffect(() => { 
        let products = [];
        if(localStorage.getItem('products')){
            products = JSON.parse(localStorage.getItem('products'));
        }
        for(let i=0; i<products.length;i++){
            if(products[i].productId === product.id){
                alert("This product is already in your cart");
                setDisabledButton(true)
            }
        }

    }, [disabledButton])
    console.log(disabledButton)

    return(
    <Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src={product.img} />
        <Card.Body>
            <NavLink to={productPath}>
                <Card.Title>{product.name}</Card.Title>                
            </NavLink>
            <p>{product.price}</p>
        <ReadMoreReact 
            readMoreText={moreTxt()}
            text={product.description}/>
          <Button variant="primary" disabled={disabledButton} onClick={clickHandler}>Add to cart</Button>
        </Card.Body>
      </Card>)
}

export default Product;
