import React,{useState} from 'react';

import {Button, Card, Col, Image, Row, } from 'react-bootstrap'
import {Link} from 'react-router-dom'




const CartItem = (props) =>{
    const [quantity,setQuantity] = useState(1)
    const [disabledButton,setDisabledButton]= useState(false)

    const addQuantityHandler = () => {
        if(quantity>0){
            setDisabledButton(false)
        }
        let newQuantity = quantity + 1;
        setQuantity(newQuantity)
        let priceChange=(props.price)
        props.priceHandler(priceChange)

    }
    const minusQuantityHandler = () => {
        if(quantity === 1){
            setDisabledButton(true)
        }
        let newQuantity = quantity - 1;
        setQuantity(newQuantity)
        let priceChange=(-props.price)
        props.priceHandler(priceChange)
    }
    const deleteHander = () =>{
        props.delete(props.index)
    }

    return (
    <Row className="mt-3">
        <Col md={2} lg={2} > 
            <Image variant="top" src={props.img} fluid rounded/>
        </Col>
        <Col md={3}>
            <Link to={`/product/${props.id}`}>{props.name}</Link>
        </Col>
        <Col md={3}>
            <Row className="justify-content-md-center">
                <Button onClick={minusQuantityHandler} disabled={disabledButton} variant="danger">-</Button>
                <p>{quantity}</p>
                <Button onClick={addQuantityHandler}>B</Button>
                {props.index}
            </Row>
            <Button onClick={deleteHander}>Deleteme</Button>
        </Col>
        <Col md={3}><p>{props.price*quantity}</p></Col>
    </Row>)
}

export default CartItem
